static void ian_scroll_screen(void){
	int i;
	char *j;
	char *screen_base = (char*)0x2000000;//1F88000;
	for(i = 8; i < 480 ; i++)
		for(j = screen_base + i*(640/2) ; j < screen_base + (i+1)*(640/2) ; j++)
			*(j-(8*(640/2))) = *j;
	for(i = 479 ; i >= 472 ; i--)
		for(j = screen_base + i*(640/2) ; j < screen_base + (i+1)*(640/2) ; j++)
			*j = 0;

}

static void ian_print(char *string){
        char *screen_base = (char*)0x2000000;
        static int x, y, first, page;
        int *temp;
        int i;

	if(first == 0){
		first = 1;
		for(i = 0 ; i < (640/2)*480 ; i++) screen_base[i] = 0;
	}

        while(*string){
                if(*string==10){
                        y++;
                        x = 0;
			if(y > 59){
				y = 59;
				ian_scroll_screen();
			}
		}
		else if(*string == 8){
			if(x)
				x--;
		}
		else{
			if(x > 79){
				y++;
				x = 0;
				if(y > 59){
					y = 59;
					ian_scroll_screen();
				}
			}
	
			temp = (int *)(screen_base + ((640/2)*8*y) + (x * 4));
			for(i = 0 ; i < 8 ; i++){
				*temp = charset[i + (*string * 8)];
				temp += 640/8;
			}
			x++;
		}
                string++;
        }
}

static void ian_print_num(unsigned long value){
        int i;
        char out[9];

        for(i = 7; i >= 0 ; i--){
                out[i] = value & 0x0F;
                out[i] = out[i]<10?out[i]+'0':out[i]+'A'-10;
                value = value >> 4;
        }
        out[8] = 0;

        ian_print(out);

}

